INSERT INTO mw_text VALUES (1329,'When you make a change to the configuration that invalidates a large number of cached pages, the easiest way to clear the cache is to simply cause MediaWiki to ignore any page older than the time at which you made the change.\n\nSimply add this configuration item to LocalSettings.php:\n\n    $wgCacheEpoch = \'<TimestampNow></TimestampNow>\';\n\n(This page requires the TimestampNow extension to be installed).','utf-8');
